mapper.insert:
insert into grade (id, grade_name, sort
  )
values (#{id,jdbcType=VARCHAR}, #{gradeName,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}
  )

mapper.update:
update grade
set grade_name = #{gradeName,jdbcType=VARCHAR},
  sort = #{sort,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, grade_name, sort
from grade
where id = #{id,jdbcType=VARCHAR}
