mapper.insert:
insert into subject (id, name, sort
  )
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}
  )

mapper.update:
update subject
set name = #{name,jdbcType=VARCHAR},
  sort = #{sort,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, name, sort
from subject
where id = #{id,jdbcType=VARCHAR}
