mapper.delete:

delete from user
where id = #{id,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=VARCHAR}

mapper.select:

select id, user_id, username, `password`, salt, nickname, email, phone, sex, age, 
img, `status`, create_time, update_time, last_login_time
from user
where id = #{id,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=VARCHAR}
